mapper.select:
SELECT
  o.*
FROM
    t_order as o
LEFT JOIN t_merchant_info as mi on mi.merchant_id = o.merchant_id
WHERE 1 = 1


        AND o.merchant_id = #{currMerchantId}


        AND mi.pl_merchant_id = #{currMerchantId}



    AND o.create_time >= #{startTime}


    AND o.create_time <= #{endTime}


    AND o.order_id = #{orderId}


    AND o.order_type = #{orderType}


    AND o.order_status = #{orderStatus}


    AND o.task_name like concat('%', #{taskName}, '%')


    AND mi.merchant_name like concat('%',#{merchantName},'%')


    AND mi.merchant_id = #{merchantId}

order by o.create_time desc , o.order_id desc
LIMIT ${(page-1)*limit},${limit}

SELECT
  count(o.order_id)
FROM
    t_order as o
LEFT JOIN t_merchant_info as mi on mi.merchant_id = o.merchant_id
WHERE 1 = 1


          AND o.merchant_id = #{currMerchantId}


          AND mi.pl_merchant_id = #{currMerchantId}



    AND o.create_time >= #{startTime}


    AND o.create_time <= #{endTime}


    AND o.order_id = #{orderId}


    AND o.order_type = #{orderType}


    AND o.order_status = #{orderStatus}


   AND o.task_name like concat('%', #{taskName}, '%')


   AND mi.merchant_name like concat('%',#{merchantName},'%')


   AND mi.merchant_id = #{merchantId}

SELECT count(o.order_id) FROM t_order as o
left join t_merchant_info as mi on mi.merchant_id = o.merchant_id
WHERE 1=1
and o.update_time >= DATE_SUB(now(),INTERVAL 30 SECOND)
AND o.merchant_id = #{merchantId}
AND mi.pl_merchant_id = #{merchantId}

SELECT o.* FROM t_order as o
left join t_merchant_info as mi on mi.merchant_id = o.merchant_id
WHERE 1=1
and o.update_time >= DATE_SUB(now(),INTERVAL 30 SECOND)
AND o.merchant_id = #{merchantId}
AND mi.pl_merchant_id = #{merchantId}
order by o.update_time desc LIMIT ${(page-1)*limit},${limit}
