mapper.select:
SELECT tri.country_code AS countryCode, tri.country AS countryName, count( 1 ) AS robotTotalNUm, SUM( IF ( tri.seal_status = 10, 1, 0 ) ) AS robotSuccessNUm, SUM( IF ( tri.bidirectional_type = 1, 1, 0 ) ) AS bidirectionalNum, SUM( IF ( tri.seal_status = 30, 1, 0 ) ) AS closureNum, SUM( IF ( tri.heartbeat_status = 10, 1, 0 ) ) AS offlineNum, sum( t.sendTotalNUm ) AS sendTotalNUm, sum( t.sendSuccessNUm ) AS sendSuccessNUm, sum( t.sendFailNUm ) AS sendFailNUm FROM ( SELECT tpmpd.robot_id, count( tpmpd.id ) AS sendTotalNUm, SUM( IF ( tpmpd.send_state = 1, 1, 0 ) ) AS sendSuccessNUm, SUM( IF ( tpmpd.send_state = 2, 1, 0 ) ) AS sendFailNUm FROM t_play_message_push tpmp LEFT JOIN t_play_message_push_detail tpmpd ON tpmp.id = tpmpd.play_msg_push_id WHERE 1 = 1 AND tpmp.play_id = #{dto.playId} AND tpmpd.robot_id != '' GROUP BY tpmpd.robot_id ) t LEFT JOIN t_robot_info tri ON t.robot_id = tri.robot_serial_no WHERE 1 = 1
AND tri.country = #{dto.countryName}
GROUP BY tri.country
