mapper.select:

where isdel='0'
and bjbh = #{bjbh}
and dept_id = #{deptId}
and bjtype = #{bjtype}
and bhxh = #{bhxh}
and xn = #{xn}
and bjmc like concat('%', #{bjmc}, '%')
and bjrych = #{bjrych}
and jbny = #{jbny}
and zbjs = #{zbjs}
and pbjs = #{pbjs}
and zljs = #{zljs}
and zbjsxm like concat('%', #{zbjsxm}, '%')
and pbjsxm like concat('%', #{pbjsxm}, '%')
and zljsxm like concat('%', #{zljsxm}, '%')
and isdel = #{isdel}
and createtime = #{createtime}

${dataScope}

order by createtime desc


and bjbh = #{bjbh}
and dept_id = #{deptId}
and bjtype = #{bjtype}
and bhxh = #{bhxh}
and xn = #{xn}
and bjmc like concat('%', #{bjmc}, '%')
and bjrych = #{bjrych}
and jbny = #{jbny}
and zbjs = #{zbjs}
and pbjs = #{pbjs}
and zljs = #{zljs}
and zbjsxm like concat('%', #{zbjsxm}, '%')
and pbjsxm like concat('%', #{pbjsxm}, '%')
and zljsxm like concat('%', #{zljsxm}, '%')
and isdel = #{isdel}
and createtime = #{createtime}

${dataScope}

order by createtime desc


where
(zbjs = #{zbjs}
or pbjs = #{pbjs}
or zljs = #{zljs}
)
and isdel = 0

${dataScope}

mapper.update:
update by_class
