mapper.update:
update sys_import_fan_record
import_fan_date = #{importFanDate}, import_fan_channel = #{importFanChannel}, import_fan_live = #{importFanLive}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, del_flag = #{delFlag},
where id = #{id}

mapper.select:
select * from sys_import_fan_record where del_flag = 0 and import_fan_date = #{importFanDate} and import_fan_channel = #{importFanChannel}
and import_fan_live = #{importFanLive}
order by id desc limit 1
