mapper.select:
select slc.id, slc.live_room, slc.live_schedul_date,slc.live_start_time, slc.live_end_time, slc.live_nutritionist_id, slc.live_operator_id, slc.live_planner_id, slc.fan_channel, slc.live_status, slc.create_time, slc.create_by ,slt.dict_label as live_room_name,su_nutritionist.nick_name as live_nutritionist_name, su_plan.nick_name as live_planner_name, su_operator.nick_name as live_operator_name,fc.dict_label as fan_channel_name from sys_live_schedul slc left join (SELECT dict_label, dict_value FROM sys_dict_data WHERE dict_type = 'sys_live_type') AS slt ON slt.dict_value = slc.live_room left join (SELECT dict_label, dict_value FROM sys_dict_data WHERE dict_type = 'fan_channel') AS fc ON fc.dict_value = slc.fan_channel left join sys_user su_nutritionist on su_nutritionist.user_id = slc.live_nutritionist_id and su_nutritionist.del_flag = 0 left join sys_user su_plan on su_plan.user_id = slc.live_planner_id and su_plan.del_flag = 0 left join sys_user su_operator on su_operator.user_id = slc.live_operator_id and su_operator.del_flag = 0 where slc.del_flag = 0
and date_format(slc.live_schedul_date,'%y%m%d') = date_format(#{liveSchedulDate},'%y%m%d')
and slc.live_room = #{liveRoom}
and slc.fan_channel = #{fanChannel}
and slc.fan_channel = (SELECT swd.account_id FROM sys_wx_distribution AS swd where swd.wechat_account = #{wxAccountId} and swd.del_flag = 0 limit 1)
order by slc.live_schedul_date desc,slc.live_start_time desc ,slc.live_room asc

mapper.update:
update sys_live_schedul
live_room = #{liveRoom}, live_schedul_date = #{liveSchedulDate}, live_start_time = #{liveStartTime}, live_end_time = #{liveEndTime}, live_nutritionist_id = #{liveNutritionistId}, live_operator_id = #{liveOperatorId}, live_planner_id = #{livePlannerId}, fan_channel = #{fanChannel}, live_status = #{liveStatus}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, del_flag = #{delFlag},
where id = #{id}

update sys_live_schedul set live_room = #{liveRoom},live_schedul_date = #{liveSchedulDate},live_start_time = #{liveStartTime},
live_end_time = #{liveEndTime},live_nutritionist_id = #{liveNutritionistId},live_operator_id = #{liveOperatorId},live_planner_id = #{livePlannerId},
fan_channel = #{fanChannel},live_status = #{liveStatus},update_time = #{updateTime}
where id = #{id}

mapper.select:
select
from sys_live_schedul where live_room = #{liveRoom} and del_flag = 0 AND ( (#{liveStartTime} >= live_start_time AND live_end_time > #{liveStartTime})
OR (#{liveEndTime} > live_start_time AND live_end_time >= #{liveEndTime})
) limit 1
