mapper.update:
update sys_order_nutritionist_replace_record
order_id = #{orderId}, nutritionist_id = #{nutritionistId}, after_sale_id = #{afterSaleId}, nutri_assis_id = #{nutriAssisId}, start_time = #{startTime}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, del_flag = #{delFlag},
where id = #{id}

mapper.select:
select id, order_id, nutritionist_id, after_sale_id, nutri_assis_id, start_time, create_time, create_by from sys_order_nutritionist_replace_record where del_flag = 0
and order_id = #{orderId}
order by id asc

select count(id) from sys_order_nutritionist_replace_record where del_flag = 0 and order_id = #{orderId}

    and nutritionist_id = #{nutritionistId}


    and nutri_assis_id = #{nutriAssisId}


    and after_sale_id = #{afterSaleId}

and start_time = #{startTime}
